def get_stores(context, chat_id, customer_address=None):

    '''
    Returns all Domino's pizza stores in Nigeria.

    ---------

    :Parameter -> customer_address: None by default, if it is passed as an argument, 
    the closest stores to the customer's address is returned

    ----------

    '''
   
    '''
    proxy = '197.157.219.169:48625'
    '''


    URL = "https://www.dominos.ng/pages/order/#!/store-list/"
    
    opt = Options()
    opt.add_argument('--headless')
   
    driver = webdriver.Chrome(options=opt)
    driver.get(URL)
    element = WebDriverWait(driver, 30).until(
        presence_of_element_located((By.CLASS_NAME, "js-search-results-list"))
    )
    r = driver.page_source
    
    
    soup = BeautifulSoup(r, "lxml")
    
    
    stores = soup.find('div','')
    
    

    for store in stores:
        address = store.find('div', class_="location-search-result__address grid__cell--1/2")
        address_header = address.find('h3').text
        address_info = address.find('ul', class_="js-list")
        address_no = address_info.findAll('li')[0].text
        full_address = address_info.findAll('li')[1].text
        telephone = address_info.findAll('li')[2].text

        hours = store.find('div', class_="location-search-result__hours grid__cell--1/2")
        serviceHours = hours.findAll('ul', class_="js-list serviceHoursList")
        service_hours = serviceHours[0].findAll('li')[0].text
        carryout_hours = serviceHours[1].findAll('li')[0].text

        try:
            delivery_link = store.find('a', class_="js-orderDeliveryNow btn btn--block")['href']
            delivery_link = 'https://www.dominos.ng/pages/order/' + str(delivery_link)
        except:
            delivery_link = None
        try:
            carryOut_link = store.find('a', class_="js-orderCarryoutNow js-carryoutAvailable btn btn--block c-orderCarryoutNow")['href']
            carryOut_link =   'https://www.dominos.ng/pages/order/' + str(carryOut_link)
        except:
            carryOut_link = None
        msg = f'{address_header}\n{address_no}, {full_address}\n\n{telephone}\nService Hours: {service_hours}\nCarryOut Hours: {carryout_hours}\nDelivery Link: {delivery_link}\nCarryout/Pickup link: {carryOut_link}'
        context.bot.send_message(chat_id=chat_id, text=msg)

        print(address_header, address_no, full_address, telephone)
        print(service_hours, carryout_hours)

    
    



